CREATE DEFINER=`root`@`localhost` PROCEDURE `SPapmsavesecurityqueston`(
IN iquestion varchar(255),
IN ianswer varchar(255),
IN iuserid int(11),
IN iaction varchar(255),
IN iisupdate INT(1),
IN iadminuserid int(11),
OUT omess varchar(255))
